#34;enabled&#34; : true, &#34;chat_settings&#34; : { &#34;greeting_message&#34; : &#34;Hi! I’m Coco, nice to meet you. I can help answer your questions by tapping into the internet and your data sources. How can I assist you today?&#34;, &#34;suggested&#34; : { &#34;enabled&#34; : false, &#34;questions&#34; : [ ] }, &#34;input_preprocess_tpl&#34; : &#34;&#34;, &#34;history_message&#34; : { &#34;number&#34; : 5, &#34;compression_threshold&#34; : 1000, &#34;summary&#34; : true } }, &#34;builtin&#34; : false, &#34;role_prompt&#34; : &#34;&#34; }&#39; //response { &#34;_id&#34;: &#34;cvuak1lath2dlgqqpcjg&#34;, &#34;result&#34;: &#34;created&#34; } Update an AI assistant #  //request curl -XPUT http://localhost:9000/assistant/cvuak1lath2dlgqqpcjg -d&#39;{ &#34;name&#34; : &#34;deault&#34;, &#34;description&#34; : &#34;default AI chat assistant&#34;, &#34;icon&#34; : &#34;font_Google-video&#34;, &#34;type&#34; : &#34;deep_think&#34;, &#34;config&#34; : { &#34;intent_analysis_model&#34; : { &#34;name&#34; : &#34;tongyi-intent-detect-v3&#34;, &#34;provider_id&#34; : &#34;cvuai3dath2dlgqqpc2g&#34;, &#34;settings&#34;: { &#34;temperature&#34; : 0.8, &#34;top_p&#34; : 0.5, &#34;presence_penalty&#34; : 0, &#34;frequency_penalty&#34; : 0, &#34;max_tokens&#34; : 1024 } }, &#34;picking_doc_model&#34; : { &#34;name&#34; : &#34;deepseek-r1-distill-qwen-32b&#34;, &#34;provider_id&#34; : &#34;cvuai3dath2dlgqqpc2g&#34;, &#34;settings&#34;: { &#34;temperature&#34; : 0.8, &#34;top_p&#34; : 0.5, &#34;presence_penalty&#34; : 0, &#34;frequency_penalty&#34; : 0, &#34;max_tokens&#34; : 1024 } } }, &#34;answering_model&#34; : { &#34;provider_id&#34; : &#34;cvuai3dath2dlgqqpc2g&#34;, &#34;name&#34; : &#34;deepseek-r1&#34;, &#34;settings&#34; : { &#34;temperature&#34; : 0.8, &#34;top_p&#34; : 0.5, &#34;presence_penalty&#34; : 0, &#34;frequency_penalty&#34; : 0, &#34;max_tokens&#34; : 1024 } }, &#34;datasource&#34; : { &#34;enabled&#34; : true, &#34;ids&#34; : [ &#34;d895f22ed2ff25ad8c6080af1cc23